package jafpl;

public class Arrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int[] chars = { 1, 2, 3, 4, 5, 6, 'A', 'Q' };
		System.out.println(chars[0] * '0');
		System.out.println(chars[1] * '1');
		System.out.println(chars[2] * '2');
		System.out.println(chars[3] * '3');
		System.out.println(chars[4] * '4');
		System.out.println(chars[5] * '5');
		System.out.println(chars[7]);

	}

}